@import url('https://fonts.googleapis.com/css2?family=Space+Grotesk:wght@300;400;500;600;700&family=Orbitron:wght@400;500;600;700;800;900&display=swap');

@tailwind base;
@tailwind components;
@tailwind utilities;

/* FUTURISTIC GLASSMORPHISM THEME - Black Background with Neon Cyan Accents */
:root {
  /* Glassmorphism Variables */
  --glass-bg: rgba(255, 255, 255, 0.05);
  --glass-bg-hover: rgba(255, 255, 255, 0.08);
  --glass-bg-active: rgba(0, 255, 255, 0.1);
  --glass-border: rgba(255, 255, 255, 0.1);
  --glass-border-hover: rgba(0, 255, 255, 0.4);

  /* Elevation & Interaction */
  --button-outline: rgba(255,255,255, .10);
  --badge-outline: rgba(255,255,255, .05);
  --opaque-button-border-intensity: 9;
  --elevate-1: rgba(0, 255, 255, .06);
  --elevate-2: rgba(0, 255, 255, .12);

  /* Pure Black Background */
  --background: 0 0% 0%;
  --foreground: 0 0% 100%;

  /* Glass Cards - Slightly lighter than pure black */
  --card: 0 0% 5%;
  --card-foreground: 0 0% 98%;
  --card-border: 0 0% 15%;

  /* Borders */
  --border: 0 0% 15%;

  /* Sidebar - Glass treatment */
  --sidebar: 0 0% 3%;
  --sidebar-foreground: 0 0% 95%;
  --sidebar-border: 0 0% 12%;
  --sidebar-primary: 180 100% 50%;
  --sidebar-primary-foreground: 0 0% 5%;
  --sidebar-accent: 0 0% 10%;
  --sidebar-accent-foreground: 0 0% 88%;
  --sidebar-ring: 180 100% 50%;

  /* Popovers */
  --popover: 0 0% 8%;
  --popover-foreground: 0 0% 95%;
  --popover-border: 0 0% 15%;

  /* Neon Cyan Primary */
  --primary: 180 100% 50%;
  --primary-foreground: 0 0% 5%;

  /* Secondary - Darker glass */
  --secondary: 0 0% 12%;
  --secondary-foreground: 0 0% 88%;

  /* Muted states */
  --muted: 0 0% 15%;
  --muted-foreground: 0 0% 65%;

  /* Accent - Electric Blue */
  --accent: 210 100% 50%;
  --accent-foreground: 0 0% 5%;

  /* Destructive */
  --destructive: 0 84% 42%;
  --destructive-foreground: 0 84% 98%;

  /* Form inputs */
  --input: 0 0% 20%;

  /* Focus ring - Cyan glow */
  --ring: 180 100% 50%;

  /* Chart colors - Neon palette */
  --chart-1: 180 100% 50%;
  --chart-2: 210 100% 60%;
  --chart-3: 160 70% 50%;
  --chart-4: 280 80% 60%;
  --chart-5: 40 100% 50%;

  /* Typography */
  --font-sans: 'Space Grotesk', -apple-system, BlinkMacSystemFont, "Segoe UI", sans-serif;
  --font-display: 'Orbitron', 'Space Grotesk', sans-serif;
  --font-serif: Georgia, serif;
  --font-mono: Menlo, monospace;

  /* Border radius */
  --radius: .75rem;

  /* Glass shadows with cyan glow */
  --shadow-2xs: 0px 2px 4px rgba(0, 0, 0, 0.4);
  --shadow-xs: 0px 4px 8px rgba(0, 0, 0, 0.4);
  --shadow-sm: 0px 6px 12px rgba(0, 0, 0, 0.4), 0px 2px 4px rgba(0, 0, 0, 0.2);
  --shadow: 0px 8px 16px rgba(0, 0, 0, 0.5), 0px 2px 6px rgba(0, 0, 0, 0.3);
  --shadow-md: 0px 12px 24px rgba(0, 0, 0, 0.5), 0px 4px 8px rgba(0, 0, 0, 0.3);
  --shadow-lg: 0px 16px 32px rgba(0, 0, 0, 0.6), 0px 6px 12px rgba(0, 0, 0, 0.4);
  --shadow-xl: 0px 24px 48px rgba(0, 0, 0, 0.7), 0px 8px 16px rgba(0, 0, 0, 0.5);
  --shadow-2xl: 0px 32px 64px rgba(0, 0, 0, 0.8);

  /* Cyan glow shadows */
  --glow-cyan: 0 0 20px rgba(0, 255, 255, 0.3), 0 0 40px rgba(0, 255, 255, 0.15);
  --glow-cyan-intense: 0 0 30px rgba(0, 255, 255, 0.5), 0 0 60px rgba(0, 255, 255, 0.25), 0 0 90px rgba(0, 255, 255, 0.1);

  --tracking-normal: 0em;
  --spacing: 0.25rem;

  /* Border colors - with fallbacks */
  --sidebar-primary-border: hsl(var(--sidebar-primary));
  --sidebar-primary-border: hsl(from hsl(var(--sidebar-primary)) h s calc(l + var(--opaque-button-border-intensity)) / alpha);

  --sidebar-accent-border: hsl(var(--sidebar-accent));
  --sidebar-accent-border: hsl(from hsl(var(--sidebar-accent)) h s calc(l + var(--opaque-button-border-intensity)) / alpha);

  --primary-border: hsl(var(--primary));
  --primary-border: hsl(from hsl(var(--primary)) h s calc(l + var(--opaque-button-border-intensity)) / alpha);

  --secondary-border: hsl(var(--secondary));
  --secondary-border: hsl(from hsl(var(--secondary)) h s calc(l + var(--opaque-button-border-intensity)) / alpha);

  --muted-border: hsl(var(--muted));
  --muted-border: hsl(from hsl(var(--muted)) h s calc(l + var(--opaque-button-border-intensity)) / alpha);

  --accent-border: hsl(var(--accent));
  --accent-border: hsl(from hsl(var(--accent)) h s calc(l + var(--opaque-button-border-intensity)) / alpha);

  --destructive-border: hsl(var(--destructive));
  --destructive-border: hsl(from hsl(var(--destructive)) h s calc(l + var(--opaque-button-border-intensity)) / alpha);
}

/* Dark mode - same as light for this black theme */
.dark {
  --button-outline: rgba(255,255,255, .10);
  --badge-outline: rgba(255,255,255, .05);
  --opaque-button-border-intensity: 9;
  --elevate-1: rgba(0, 255, 255, .06);
  --elevate-2: rgba(0, 255, 255, .12);
  --background: 0 0% 0%;
  --foreground: 0 0% 100%;
  --border: 0 0% 15%;
  --card: 0 0% 5%;
  --card-foreground: 0 0% 98%;
  --card-border: 0 0% 15%;
  --sidebar: 0 0% 3%;
  --sidebar-foreground: 0 0% 95%;
  --sidebar-border: 0 0% 12%;
  --sidebar-primary: 180 100% 50%;
  --sidebar-primary-foreground: 0 0% 5%;
  --sidebar-accent: 0 0% 10%;
  --sidebar-accent-foreground: 0 0% 88%;
  --sidebar-ring: 180 100% 50%;
  --popover: 0 0% 8%;
  --popover-foreground: 0 0% 95%;
  --popover-border: 0 0% 15%;
  --primary: 180 100% 50%;
  --primary-foreground: 0 0% 5%;
  --secondary: 0 0% 12%;
  --secondary-foreground: 0 0% 88%;
  --muted: 0 0% 15%;
  --muted-foreground: 0 0% 65%;
  --accent: 210 100% 50%;
  --accent-foreground: 0 0% 5%;
  --destructive: 0 84% 42%;
  --destructive-foreground: 0 84% 98%;
  --input: 0 0% 20%;
  --ring: 180 100% 50%;
  --chart-1: 180 100% 50%;
  --chart-2: 210 100% 60%;
  --chart-3: 160 70% 50%;
  --chart-4: 280 80% 60%;
  --chart-5: 40 100% 50%;

  --sidebar-primary-border: hsl(var(--sidebar-primary));
  --sidebar-primary-border: hsl(from hsl(var(--sidebar-primary)) h s calc(l + var(--opaque-button-border-intensity)) / alpha);
  --sidebar-accent-border: hsl(var(--sidebar-accent));
  --sidebar-accent-border: hsl(from hsl(var(--sidebar-accent)) h s calc(l + var(--opaque-button-border-intensity)) / alpha);
  --primary-border: hsl(var(--primary));
  --primary-border: hsl(from hsl(var(--primary)) h s calc(l + var(--opaque-button-border-intensity)) / alpha);
  --secondary-border: hsl(var(--secondary));
  --secondary-border: hsl(from hsl(var(--secondary)) h s calc(l + var(--opaque-button-border-intensity)) / alpha);
  --muted-border: hsl(var(--muted));
  --muted-border: hsl(from hsl(var(--muted)) h s calc(l + var(--opaque-button-border-intensity)) / alpha);
  --accent-border: hsl(var(--accent));
  --accent-border: hsl(from hsl(var(--accent)) h s calc(l + var(--opaque-button-border-intensity)) / alpha);
  --destructive-border: hsl(var(--destructive));
  --destructive-border: hsl(from hsl(var(--destructive)) h s calc(l + var(--opaque-button-border-intensity)) / alpha);
}

@layer base {
  * {
    @apply border-border;
  }

  body {
    @apply font-sans antialiased bg-background text-foreground;
    background-color: #000000;
  }

  h1, h2, h3, h4, h5, h6 {
    @apply font-display;
  }
}

/**
 * Glassmorphism and Neon Effects Utilities
 */
@layer utilities {

  /* Hide ugly search cancel button in Chrome until we can style it properly */
  input[type="search"]::-webkit-search-cancel-button {
    @apply hidden;
  }

  /* Placeholder styling for contentEditable div */
  [contenteditable][data-placeholder]:empty::before {
    content: attr(data-placeholder);
    color: hsl(var(--muted-foreground));
    pointer-events: none;
  }

  /* Glassmorphism card style */
  .glass-card {
    background: var(--glass-bg);
    backdrop-filter: blur(20px);
    -webkit-backdrop-filter: blur(20px);
    border: 1px solid var(--glass-border);
    transition: all 0.3s cubic-bezier(0.4, 0, 0.2, 1);
  }

  .glass-card:hover {
    background: var(--glass-bg-hover);
    border-color: var(--glass-border-hover);
    transform: translateY(-2px);
  }

  /* Neon cyan glow effect */
  .neon-glow {
    box-shadow: var(--glow-cyan);
    transition: box-shadow 0.3s ease, transform 0.2s ease;
  }

  .neon-glow:hover {
    box-shadow: var(--glow-cyan-intense);
    transform: translateY(-1px);
  }

  /* Neon border glow */
  .neon-border {
    position: relative;
  }

  .neon-border::before {
    content: "";
    position: absolute;
    inset: -2px;
    background: linear-gradient(45deg, rgba(0, 255, 255, 0.5), rgba(0, 128, 255, 0.5));
    border-radius: inherit;
    opacity: 0;
    transition: opacity 0.3s ease;
    z-index: -1;
    filter: blur(8px);
  }

  .neon-border:hover::before {
    opacity: 1;
  }

  /* Pop effect for sidebar items */
  .pop-hover {
    transition: transform 0.2s cubic-bezier(0.4, 0, 0.2, 1), box-shadow 0.2s ease;
  }

  .pop-hover:hover {
    transform: scale(1.05) translateX(4px);
    box-shadow: -4px 0 0 0 hsl(var(--primary));
  }

  /* No default interaction styles escape hatch */
  .no-default-hover-elevate {}
  .no-default-active-elevate {}

  /**
   * Toggle elevation system - glassmorphism version
   */
  .toggle-elevate::before,
  .toggle-elevate-2::before {
    content: "";
    pointer-events: none;
    position: absolute;
    inset: 0px;
    border-radius: inherit;
    z-index: -1;
  }

  .toggle-elevate.toggle-elevated::before {
    background-color: var(--elevate-2);
  }

  .border.toggle-elevate::before {
    inset: -1px;
  }

  /**
   * Hover/Active elevation system - neon cyan version
   */
  .hover-elevate:not(.no-default-hover-elevate),
  .active-elevate:not(.no-default-active-elevate),
  .hover-elevate-2:not(.no-default-hover-elevate),
  .active-elevate-2:not(.no-default-active-elevate) {
    position: relative;
    z-index: 0;
  }

  .hover-elevate:not(.no-default-hover-elevate)::after,
  .active-elevate:not(.no-default-active-elevate)::after,
  .hover-elevate-2:not(.no-default-hover-elevate)::after,
  .active-elevate-2:not(.no-default-active-elevate)::after {
    content: "";
    pointer-events: none;
    position: absolute;
    inset: 0px;
    border-radius: inherit;
    z-index: 999;
  }

  .hover-elevate:hover:not(.no-default-hover-elevate)::after,
  .active-elevate:active:not(.no-default-active-elevate)::after {
    background-color: var(--elevate-1);
  }

  .hover-elevate-2:hover:not(.no-default-hover-elevate)::after,
  .active-elevate-2:active:not(.no-default-active-elevate)::after {
    background-color: var(--elevate-2);
  }

  .border.hover-elevate:not(.no-hover-interaction-elevate)::after,
  .border.active-elevate:not(.no-active-interaction-elevate)::after,
  .border.hover-elevate-2:not(.no-hover-interaction-elevate)::after,
  .border.active-elevate-2:not(.no-active-interaction-elevate)::after,
  .border.hover-elevate:not(.no-hover-interaction-elevate)::after {
    inset: -1px;
  }

  /* Glow effect for primary CTAs */
  .glow-primary {
    transition: box-shadow 0.3s ease, transform 0.2s ease;
    box-shadow: 0 0 15px rgba(0, 255, 255, 0.2);
  }

  .glow-primary:hover {
    box-shadow: var(--glow-cyan-intense);
    transform: translateY(-2px);
  }

  /* Subtle glow for cards */
  .glow-subtle {
    transition: box-shadow 0.3s ease, transform 0.2s ease;
  }

  .glow-subtle:hover {
    box-shadow: 0 4px 20px rgba(0, 255, 255, 0.15), 0 8px 40px rgba(0, 0, 0, 0.5);
    transform: translateY(-2px);
  }

  /* Smooth transitions for all interactive elements */
  button, a, [role="button"] {
    transition: all 0.2s cubic-bezier(0.4, 0, 0.2, 1);
  }

  /* Pop animation on click */
  .pop-on-click:active {
    transform: scale(0.97);
  }

  /* Fade in animation */
  @keyframes fadeIn {
    from {
      opacity: 0;
      transform: translateY(10px);
    }
    to {
      opacity: 1;
      transform: translateY(0);
    }
  }

  .animate-fade-in {
    animation: fadeIn 0.3s ease;
  }

  /* Pulse animation for neon elements */
  @keyframes pulse-neon {
    0%, 100% {
      opacity: 1;
      box-shadow: var(--glow-cyan);
    }
    50% {
      opacity: 0.9;
      box-shadow: var(--glow-cyan-intense);
    }
  }

  .animate-pulse-neon {
    animation: pulse-neon 2s cubic-bezier(0.4, 0, 0.6, 1) infinite;
  }

  /* Float animation for parallax elements */
  @keyframes float {
    0%, 100% {
      transform: translateY(0px) rotate(0deg);
    }
    33% {
      transform: translateY(-20px) rotate(5deg);
    }
    66% {
      transform: translateY(10px) rotate(-5deg);
    }
  }

  .animate-float {
    animation: float 6s ease-in-out infinite;
  }

  /* Rotate animation for slow spinning elements */
  @keyframes rotate-slow {
    from {
      transform: rotate(0deg);
    }
    to {
      transform: rotate(360deg);
    }
  }

  .animate-rotate-slow {
    animation: rotate-slow 60s linear infinite;
  }

  /* Backdrop blur utility for glassmorphism */
  .backdrop-blur-glass {
    backdrop-filter: blur(20px);
    -webkit-backdrop-filter: blur(20px);
  }

  .backdrop-blur-glass-strong {
    backdrop-filter: blur(40px);
    -webkit-backdrop-filter: blur(40px);
  }
}

