client/src/pages/SchedulePage.tsx.content:
import { useState } from "react";
import { useQuery, useMutation } from "@tanstack/react-query";
import DashboardLayout from "@/components/DashboardLayout";
import { Card } from "@/components/ui/card";
import { Button } from "@/components/ui/button";
import { Badge } from "@/components/ui/badge";
import { Calendar as CalendarIcon, Clock, BookOpen, FileQuestion, Loader2, Plus, CheckCircle2 } from "lucide-react";
import { apiRequest, queryClient } from "@/lib/queryClient";
import { useToast } from "@/hooks/use-toast";
import {
  Dialog,
  DialogContent,
  DialogDescription,
  DialogHeader,
  DialogTitle,
  DialogTrigger,
} from "@/components/ui/dialog";
import { Textarea } from "@/components/ui/textarea";

interface ScheduleItem {
  id: string;
  title: string;
  type: 'module' | 'quiz' | 'practice';
  date: string;
  time: string;
  duration: string;
  course?: string;
  completed: boolean;
}

export default function SchedulePage() {
  const [generatingSchedule, setGeneratingSchedule] = useState(false);
  const [learningGoals, setLearningGoals] = useState("");
  const [dialogOpen, setDialogOpen] = useState(false);
  const { toast } = useToast();

  const userId = localStorage.getItem('userId') || 'demo-user-123';
  const userName = localStorage.getItem('userName') || 'User';

  // Fetch schedule
  const { data: scheduleData, isLoading } = useQuery<{ items: ScheduleItem[] }>({
    queryKey: ['/api/schedule', userId],
    enabled: !!userId,
  });

  // Generate schedule mutation
  const generateScheduleMutation = useMutation({
    mutationFn: async (goals: string) => {
      const res = await apiRequest('POST', '/api/schedule/generate', {
        userId,
        goals,
      });
      return await res.json();
    },
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ['/api/schedule', userId] });
      toast({
        title: "Schedule created!",
        description: "Your personalized learning schedule is ready",
      });
      setDialogOpen(false);
      setLearningGoals("");
    },
  });

  // Mark item complete mutation
  const completeMutation = useMutation({
    mutationFn: async (itemId: string) => {
      const res = await apiRequest('POST', `/api/schedule/${itemId}/complete`, {
        userId,
      });
      return await res.json();
    },
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ['/api/schedule', userId] });
      toast({
        title: "Great job!",
        description: "Schedule item marked as complete",
      });
    },
  });

  const handleGenerateSchedule = () => {
    if (!learningGoals.trim()) return;
    generateScheduleMutation.mutate(learningGoals);
  };

  const scheduleItems = scheduleData?.items || [];
  const upcomingItems = scheduleItems.filter(item => !item.completed);
  const completedItems = scheduleItems.filter(item => item.completed);

  const getTypeIcon = (type: string) => {
    switch (type) {
      case 'module':
        return <BookOpen className="h-4 w-4" />;
      case 'quiz':
        return <FileQuestion className="h-4 w-4" />;
      default:
        return <CalendarIcon className="h-4 w-4" />;
    }
  };

  const getTypeBadge = (type: string) => {
    const colors = {
      module: 'bg-chart-1/10 text-chart-1 border-chart-1/20',
      quiz: 'bg-chart-3/10 text-chart-3 border-chart-3/20',
      practice: 'bg-chart-5/10 text-chart-5 border-chart-5/20',
    };
    return colors[type as keyof typeof colors] || '';
  };

  const ScheduleItemCard = ({ item }: { item: ScheduleItem }) => (
    <Card className={`p-4 hover-elevate transition-all ${item.completed ? 'opacity-60' : ''}`}>
      <div className="flex items-start gap-4">
        <div className={`p-2 rounded-md ${item.completed ? 'bg-muted' : 'bg-primary/10'}`}>
          {getTypeIcon(item.type)}
        </div>

        <div className="flex-1 min-w-0">
          <div className="flex items-start justify-between gap-2 mb-2">
            <div className="flex-1">
              <h3 className="font-semibold leading-tight mb-1" data-testid={`schedule-item-title-${item.id}`}>
                {item.title}
              </h3>
              {item.course && (
                <p className="text-sm text-muted-foreground">{item.course}</p>
              )}
            </div>
            <Badge variant="outline" className={getTypeBadge(item.type)}>
              {item.type}
            </Badge>
          </div>

          <div className="flex flex-wrap items-center gap-3 text-sm text-muted-foreground">
            <div className="flex items-center gap-1">
              <CalendarIcon className="h-3 w-3" />
              {item.date}
            </div>
            <div className="flex items-center gap-1">
              <Clock className="h-3 w-3" />
              {item.time}
            </div>
            <span>Â·</span>
            <span>{item.duration}</span>
          </div>
        </div>

        {!item.completed && (
          <Button
            size="sm"
            variant="outline"
            onClick={() => completeMutation.mutate(item.id)}
            disabled={completeMutation.isPending}
            data-testid={`button-complete-${item.id}`}
          >
            <CheckCircle2 className="h-4 w-4 mr-1" />
            Complete
          </Button>
        )}
      </div>
    </Card>
  );

  return (
    <DashboardLayout userName={userName}>
      <div className="space-y-6">
        <div className="flex justify-between items-start gap-4">
          <div>
            <h1 className="font-display font-bold text-3xl mb-2" data-testid="page-title">
              Learning Schedule
            </h1>
            <p className="text-muted-foreground">
              AI-generated schedule to keep you on track
            </p>
          </div>

          <Dialog open={dialogOpen} onOpenChange={setDialogOpen}>
            <DialogTrigger asChild>
              <Button data-testid="button-generate-schedule">
                <Plus className="h-4 w-4 mr-2" />
                Generate Schedule
              </Button>
            </DialogTrigger>
            <DialogContent>
              <DialogHeader>
                <DialogTitle>Generate Learning Schedule</DialogTitle>
                <DialogDescription>
                  Tell me about your learning goals and I'll create a personalized schedule for you.
                </DialogDescription>
              </DialogHeader>
              <div className="space-y-4">
                <Textarea
                  placeholder="Example: I want to learn React in 4 weeks, studying 2 hours per day..."
                  value={learningGoals}
                  onChange={(e) => setLearningGoals(e.target.value)}
                  className="min-h-[120px]"
                  data-testid="input-learning-goals"
                />
                <Button
                  className="w-full"
                  onClick={handleGenerateSchedule}
                  disabled={!learningGoals.trim() || generateScheduleMutation.isPending}
                  data-testid="button-create-schedule"
                >
                  {generateScheduleMutation.isPending ? (
                    <>
                      <Loader2 className="h-4 w-4 mr-2 animate-spin" />
                      Generating...
                    </>
                  ) : (
                    <>
                      <CalendarIcon className="h-4 w-4 mr-2" />
                      Create Schedule
                    </>
                  )}
                </Button>
              </div>
            </DialogContent>
          </Dialog>
        </div>

        {isLoading ? (
          <div className="flex items-center justify-center py-12">
            <Loader2 className="h-8 w-8 animate-spin text-primary" />
          </div>
        ) : scheduleItems.length === 0 ? (
          <Card className="p-12 text-center">
            <CalendarIcon className="h-12 w-12 mx-auto mb-4 text-muted-foreground" />
            <h3 className="font-semibold text-lg mb-2">No schedule yet</h3>
            <p className="text-muted-foreground mb-4">
              Let AI create a personalized learning schedule based on your goals
            </p>
            <Button onClick={() => setDialogOpen(true)} data-testid="button-create-first-schedule">
              <Plus className="h-4 w-4 mr-2" />
              Create Your First Schedule
            </Button>
          </Card>
        ) : (
          <div className="space-y-6">
            {upcomingItems.length > 0 && (
              <div>
                <h2 className="font-display font-semibold text-xl mb-4">
                  Upcoming ({upcomingItems.length})
                </h2>
                <div className="space-y-3">
                  {upcomingItems.map(item => (
                    <ScheduleItemCard key={item.id} item={item} />
                  ))}
                </div>
              </div>
            )}

            {completedItems.length > 0 && (
              <div>
                <h2 className="font-display font-semibold text-xl mb-4">
                  Completed ({completedItems.length})
                </h2>
                <div className="space-y-3">
                  {completedItems.map(item => (
                    <ScheduleItemCard key={item.id} item={item} />
                  ))}
                </div>
              </div>
            )}
          </div>
        )}
      </div>
    </DashboardLayout>
  );
}

client/src/pages/MentorPage.tsx.content:
import { useState, useRef, useEffect } from "react";
import { useMutation } from "@tanstack/react-query";
import DashboardLayout from "@/components/DashboardLayout";
import { Card } from "@/components/ui/card";
import { Button } from "@/components/ui/button";
import { Input } from "@/components/ui/input";
import { Avatar, AvatarFallback } from "@/components/ui/avatar";
import { Send, Bot, User, Loader2, Sparkles } from "lucide-react";
import { apiRequest } from "@/lib/queryClient";

interface Message {
  id: string;
  role: 'user' | 'assistant';
  content: string;
  timestamp: Date;
}

export default function MentorPage() {
  const [messages, setMessages] = useState<Message[]>([
    {
      id: '1',
      role: 'assistant',
      content: "Hi! I'm your AI learning mentor. I'm here to help you achieve your learning goals. How can I assist you today?",
      timestamp: new Date()
    }
  ]);
  const [input, setInput] = useState("");
  const messagesEndRef = useRef<HTMLDivElement>(null);

  const userId = localStorage.getItem('userId') || 'demo-user-123';
  const userName = localStorage.getItem('userName') || 'User';

  const chatMutation = useMutation({
    mutationFn: async (userMessage: string) => {
      const res = await apiRequest('POST', '/api/chat', {
        userId,
        message: userMessage,
        conversationHistory: messages.map(m => ({ role: m.role, content: m.content }))
      });

      if (!res.ok) {
        throw new Error('Failed to get response from AI Mentor');
      }

      return await res.json();
    },
    onSuccess: (data) => {
      if (data.response) {
        const assistantMessage: Message = {
          id: Date.now().toString(),
          role: 'assistant',
          content: data.response,
          timestamp: new Date()
        };
        setMessages(prev => [...prev, assistantMessage]);
      }
    },
    onError: (error) => {
      // Add error message to chat
      const errorMessage: Message = {
        id: Date.now().toString(),
        role: 'assistant',
        content: "I'm having trouble connecting right now, but I'm still here to help! Try asking your question again, or explore the platform features like taking a quiz or browsing courses while I reconnect.",
        timestamp: new Date()
      };
      setMessages(prev => [...prev, errorMessage]);
      console.error('Chat error:', error);
    },
  });

  const handleSubmit = (e: React.FormEvent) => {
    e.preventDefault();
    if (!input.trim() || chatMutation.isPending) return;

    const userMessage: Message = {
      id: Date.now().toString(),
      role: 'user',
      content: input.trim(),
      timestamp: new Date()
    };

    setMessages(prev => [...prev, userMessage]);
    setInput("");
    chatMutation.mutate(input.trim());
  };

  useEffect(() => {
    // Scroll to bottom when messages change
    messagesEndRef.current?.scrollIntoView({ behavior: 'smooth' });
  }, [messages]);

  return (
    <DashboardLayout userName={userName}>
      <div className="h-[calc(100vh-8rem)] max-w-5xl mx-auto">
        <Card className="flex flex-col h-full">
          <div className="p-6 border-b bg-card/50 backdrop-blur-sm">
            <div className="flex items-center gap-4">
              <div className="p-3 rounded-full bg-primary/10">
                <Sparkles className="h-6 w-6 text-primary" />
              </div>
              <div>
                <h3 className="font-semibold text-lg text-foreground">Your Personal AI Mentor</h3>
                <p className="text-base text-muted-foreground">Always here to help you learn and grow</p>
              </div>
            </div>
          </div>

          <div className="flex-1 overflow-y-auto p-6">
            <div className="space-y-6">
              {messages.map((message) => (
                <div
                  key={message.id}
                  className={`flex gap-3 ${message.role === 'user' ? 'flex-row-reverse' : 'flex-row'}`}
                  data-testid={`message-${message.role}-${message.id}`}
                >
                  <Avatar className="h-10 w-10 flex-shrink-0">
                    <AvatarFallback className={message.role === 'assistant' ? 'bg-primary text-primary-foreground' : 'bg-accent'}>
                      {message.role === 'assistant' ? <Bot className="h-5 w-5" /> : <User className="h-5 w-5" />}
                    </AvatarFallback>
                  </Avatar>

                  <div className={`flex-1 max-w-[85%] ${message.role === 'user' ? 'text-right' : ''}`}>
                    <div
                      className={`inline-block px-5 py-4 rounded-2xl ${
                        message.role === 'user'
                          ? 'bg-primary text-primary-foreground neon-glow'
                          : 'glass-card border border-border/50'
                      }`}
                    >
                      <p className="text-base leading-relaxed whitespace-pre-wrap">{message.content}</p>
                    </div>
                    <p className="text-sm text-muted-foreground mt-2 px-2">
                      {message.timestamp.toLocaleTimeString([], { hour: '2-digit', minute: '2-digit' })}
                    </p>
                  </div>
                </div>
              ))}

              {chatMutation.isPending && (
                <div className="flex gap-3">
                  <Avatar className="h-10 w-10">
                    <AvatarFallback className="bg-primary text-primary-foreground">
                      <Bot className="h-5 w-5" />
                    </AvatarFallback>
                  </Avatar>
                  <div className="flex-1">
                    <div className="inline-block px-5 py-4 rounded-2xl glass-card border border-border/50">
                      <Loader2 className="h-5 w-5 animate-spin text-primary" />
                    </div>
                  </div>
                </div>
              )}

              {/* Invisible div to scroll to */}
              <div ref={messagesEndRef} />
            </div>
          </div>

          <form onSubmit={handleSubmit} className="p-6 border-t bg-card/50 backdrop-blur-sm">
            <div className="flex gap-3">
              <Input
                value={input}
                onChange={(e) => setInput(e.target.value)}
                placeholder="Ask anything about your learning journey..."
                disabled={chatMutation.isPending}
                className="flex-1 h-12 text-base px-4"
                data-testid="input-chat-message"
              />
              <Button
                type="submit"
                disabled={!input.trim() || chatMutation.isPending}
                className="h-12 px-6 glow-primary"
                data-testid="button-send-message"
              >
                <Send className="h-5 w-5" />
              </Button>
            </div>
          </form>
        </Card>
      </div>
    </DashboardLayout>
  );
}

client/src/pages/CoursesPage.tsx.content:
import { useState } from "react";
import { useQuery, useMutation } from "@tanstack/react-query";
import DashboardLayout from "@/components/DashboardLayout";
import { Card } from "@/components/ui/card";
import { Button } from "@/components/ui/button";
import { Badge } from "@/components/ui/badge";
import { Input } from "@/components/ui/input";
import { Tabs, TabsContent, TabsList, TabsTrigger } from "@/components/ui/tabs";
import { 
  BookOpen, 
  Clock, 
  Star, 
  ExternalLink, 
  Filter, 
  Search, 
  TrendingUp,
  DollarSign,
  CheckCircle2,
  Loader2
} from "lucide-react";
import { apiRequest, queryClient } from "@/lib/queryClient";
import { useToast } from "@/hooks/use-toast";

interface Course {
  id: string;
  title: string;
  provider: string;
  url: string;
  domain: string;
  skillLevel: string;
  price: number;
  rating: number;
  duration: string;
  description: string;
  isFree: boolean;
  courseId?: string; // For enrolled courses from database
}

export default function CoursesPage() {
  const [searchQuery, setSearchQuery] = useState("");
  const [selectedDomain, setSelectedDomain] = useState<string | null>(null);
  const { toast } = useToast();

  const userId = localStorage.getItem('userId') || 'demo-user-123';
  const userName = localStorage.getItem('userName') || 'User';
  const selectedDomains = JSON.parse(localStorage.getItem('selectedDomains') || '[]');

  // Fetch user skill levels
  const { data: skillLevels = {} } = useQuery<Record<string, string>>({
    queryKey: ['/api/user', userId, 'skills'],
    enabled: !!userId,
  });

  // Fetch recommended courses
  const { data: coursesData, isLoading } = useQuery<{ courses: Course[] }>({
    queryKey: selectedDomain 
      ? ['/api/courses/recommendations', userId, selectedDomain]
      : ['/api/courses/recommendations', userId],
    enabled: !!userId,
  });

  // Fetch enrolled courses
  const { data: enrolledCoursesData, isLoading: isLoadingEnrolled } = useQuery<{ courses: Course[] }>({
    queryKey: ['/api/user', userId, 'enrolled'],
    enabled: !!userId,
  });

  // Enroll in course mutation
  const enrollMutation = useMutation({
    mutationFn: async (course: Course) => {
      const res = await apiRequest('POST', '/api/courses/enroll', {
        userId,
        courseId: course.id,
        title: course.title,
        provider: course.provider,
        url: course.url,
        domain: course.domain,
        isPaid: !course.isFree,
        // Include all course details for proper display after enrollment
        description: course.description,
        skillLevel: course.skillLevel,
        duration: course.duration,
        rating: course.rating,
        price: course.price,
      });
      return await res.json();
    },
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ['/api/user', userId, 'enrolled'] });
      queryClient.invalidateQueries({ queryKey: ['/api/courses/recommendations'] });
      toast({
        title: "Enrolled successfully!",
        description: "Course added to your learning path",
      });
    },
  });

  const courses = coursesData?.courses || [];
  const enrolledCourses = enrolledCoursesData?.courses || [];

  // Helper to check if course is enrolled
  const isEnrolledCourse = (courseId: string) => {
    return enrolledCourses.some(ec => ec.courseId === courseId);
  };

  // Filter courses
  const filteredCourses = courses.filter(course => {
    const matchesSearch = (course.title?.toLowerCase() || '').includes(searchQuery.toLowerCase()) ||
                         (course.provider?.toLowerCase() || '').includes(searchQuery.toLowerCase());
    const matchesDomain = !selectedDomain || course.domain === selectedDomain;
    return matchesSearch && matchesDomain;
  });

  const filteredEnrolledCourses = enrolledCourses.filter(course => {
    const matchesSearch = (course.title?.toLowerCase() || '').includes(searchQuery.toLowerCase()) ||
                         (course.provider?.toLowerCase() || '').includes(searchQuery.toLowerCase());
    const matchesDomain = !selectedDomain || course.domain === selectedDomain;
    return matchesSearch && matchesDomain;
  });

  // Separate free and paid courses
  const freeCourses = filteredCourses.filter(c => c.isFree);
  const paidCourses = filteredCourses.filter(c => !c.isFree);

  const CourseCard = ({ course, isEnrolled = false }: { course: Course; isEnrolled?: boolean }) => {
    const skillLevel = skillLevels[course.domain] || 'Not assessed';

    return (
      <div className="glass-card p-6 rounded-2xl neon-border group">
        <div className="flex justify-between items-start gap-4 mb-4">
          <div className="flex-1">
            <div className="flex items-start justify-between gap-2 mb-2">
              <h3 className="font-display font-semibold text-lg leading-tight text-foreground group-hover:text-primary transition-colors" data-testid={`course-title-${course.id}`}>
                {course.title}
              </h3>
              {course.isFree && (
                <Badge variant="outline" className="bg-chart-3/20 text-chart-3 border-chart-3/40 backdrop-blur-sm flex-shrink-0">
                  Free
                </Badge>
              )}
              {isEnrolled && (
                <Badge variant="outline" className="bg-primary/20 text-primary border-primary/40 backdrop-blur-sm flex-shrink-0 animate-pulse-neon">
                  Enrolled
                </Badge>
              )}
            </div>
            <p className="text-sm text-muted-foreground mb-3">
              {course.provider}
            </p>
          </div>
        </div>

        <p className="text-sm text-foreground/80 mb-4 line-clamp-2">
          {course.description}
        </p>

        <div className="flex flex-wrap items-center gap-3 mb-4">
          <Badge variant="outline" className="bg-primary/10 text-primary border-primary/30 backdrop-blur-sm">
            {course.skillLevel}
          </Badge>
          <div className="flex items-center gap-1 text-sm text-muted-foreground">
            <Clock className="h-4 w-4" />
            {course.duration}
          </div>
          <div className="flex items-center gap-1 text-sm text-muted-foreground">
            <Star className="h-4 w-4 fill-current text-yellow-400" />
            {course.rating}
          </div>
          {!course.isFree && (
            <div className="flex items-center gap-1 text-sm font-medium text-primary">
              <DollarSign className="h-4 w-4" />
              {course.price}
            </div>
          )}
        </div>

        <div className="flex gap-2">
          {!isEnrolled && (
            <Button
              className="flex-1 glow-primary"
              onClick={() => enrollMutation.mutate(course)}
              disabled={enrollMutation.isPending}
              data-testid={`button-enroll-${course.id}`}
            >
              {enrollMutation.isPending ? (
                <Loader2 className="h-4 w-4 animate-spin mr-2" />
              ) : (
                <CheckCircle2 className="h-4 w-4 mr-2" />
              )}
              Enroll Now
            </Button>
          )}
          <Button
            variant={isEnrolled ? "default" : "outline"}
            size={isEnrolled ? "default" : "icon"}
            className={isEnrolled ? "flex-1 glow-primary" : ""}
            asChild
            data-testid={`button-view-course-${course.id}`}
          >
            <a href={course.url} target="_blank" rel="noopener noreferrer">
              <ExternalLink className="h-4 w-4" />
              {isEnrolled && <span className="ml-2">Continue Learning</span>}
            </a>
          </Button>
        </div>
      </div>
    );
  };

  return (
    <DashboardLayout userName={userName}>
      <div className="space-y-6">
        <div>
          <h1 className="font-display font-bold text-3xl mb-2" data-testid="page-title">
            Course Recommendations
          </h1>
          <p className="text-muted-foreground">
            Personalized courses based on your skill assessments
          </p>
        </div>

        {/* Search and Filters */}
        <div className="flex flex-col sm:flex-row gap-4">
          <div className="relative flex-1">
            <Search className="absolute left-3 top-1/2 -translate-y-1/2 h-4 w-4 text-muted-foreground" />
            <Input
              placeholder="Search courses..."
              value={searchQuery}
              onChange={(e) => setSearchQuery(e.target.value)}
              className="pl-9"
              data-testid="input-search-courses"
            />
          </div>
          <div className="flex gap-2 overflow-x-auto pb-2">
            <Button
              variant={selectedDomain === null ? "default" : "outline"}
              size="sm"
              onClick={() => setSelectedDomain(null)}
              data-testid="filter-all"
            >
              All Domains
            </Button>
            {selectedDomains.map((domain: string) => (
              <Button
                key={domain}
                variant={selectedDomain === domain ? "default" : "outline"}
                size="sm"
                onClick={() => setSelectedDomain(domain)}
                data-testid={`filter-${domain.toLowerCase().replace(/\s+/g, '-')}`}
              >
                {domain}
              </Button>
            ))}
          </div>
        </div>

        {/* Course Tabs */}
        <Tabs defaultValue="enrolled" className="w-full">
          <TabsList className="w-full sm:w-auto">
            <TabsTrigger value="enrolled" className="flex-1 sm:flex-none" data-testid="tab-enrolled-courses">
              <CheckCircle2 className="h-4 w-4 mr-2" />
              My Courses ({enrolledCourses.length})
            </TabsTrigger>
            <TabsTrigger value="free" className="flex-1 sm:flex-none" data-testid="tab-free-courses">
              <BookOpen className="h-4 w-4 mr-2" />
              Free ({freeCourses.length})
            </TabsTrigger>
            <TabsTrigger value="paid" className="flex-1 sm:flex-none" data-testid="tab-paid-courses">
              <TrendingUp className="h-4 w-4 mr-2" />
              Premium ({paidCourses.length})
            </TabsTrigger>
            <TabsTrigger value="all" className="flex-1 sm:flex-none" data-testid="tab-all-courses">
              All ({filteredCourses.length})
            </TabsTrigger>
          </TabsList>

          {isLoading || isLoadingEnrolled ? (
            <div className="flex items-center justify-center py-12">
              <Loader2 className="h-8 w-8 animate-spin text-primary" />
            </div>
          ) : (
            <>
              <TabsContent value="enrolled" className="mt-6">
                {filteredEnrolledCourses.length === 0 ? (
                  <Card className="p-8 text-center">
                    <CheckCircle2 className="h-12 w-12 mx-auto mb-4 text-muted-foreground" />
                    <p className="text-muted-foreground mb-2">No enrolled courses yet</p>
                    <p className="text-sm text-muted-foreground">Browse recommended courses below and enroll to start learning!</p>
                  </Card>
                ) : (
                  <div className="grid md:grid-cols-2 lg:grid-cols-3 gap-6">
                    {filteredEnrolledCourses.map(course => (
                      <CourseCard key={course.id} course={course} isEnrolled={true} />
                    ))}
                  </div>
                )}
              </TabsContent>

              <TabsContent value="free" className="mt-6">
                {freeCourses.length === 0 ? (
                  <Card className="p-8 text-center">
                    <BookOpen className="h-12 w-12 mx-auto mb-4 text-muted-foreground" />
                    <p className="text-muted-foreground">No free courses found matching your criteria</p>
                  </Card>
                ) : (
                  <div className="grid md:grid-cols-2 lg:grid-cols-3 gap-6">
                    {freeCourses.map(course => (
                      <CourseCard key={course.id} course={course} isEnrolled={isEnrolledCourse(course.id)} />
                    ))}
                  </div>
                )}
              </TabsContent>

              <TabsContent value="paid" className="mt-6">
                {paidCourses.length === 0 ? (
                  <Card className="p-8 text-center">
                    <DollarSign className="h-12 w-12 mx-auto mb-4 text-muted-foreground" />
                    <p className="text-muted-foreground">No premium courses found matching your criteria</p>
                  </Card>
                ) : (
                  <div className="grid md:grid-cols-2 lg:grid-cols-3 gap-6">
                    {paidCourses.map(course => (
                      <CourseCard key={course.id} course={course} isEnrolled={isEnrolledCourse(course.id)} />
                    ))}
                  </div>
                )}
              </TabsContent>

              <TabsContent value="all" className="mt-6">
                {filteredCourses.length === 0 ? (
                  <Card className="p-8 text-center">
                    <Filter className="h-12 w-12 mx-auto mb-4 text-muted-foreground" />
                    <p className="text-muted-foreground">No courses found matching your criteria</p>
                  </Card>
                ) : (
                  <div className="grid md:grid-cols-2 lg:grid-cols-3 gap-6">
                    {filteredCourses.map(course => (
                      <CourseCard key={course.id} course={course} isEnrolled={isEnrolledCourse(course.id)} />
                    ))}
                  </div>
                )}
              </TabsContent>
            </>
          )}
        </Tabs>
      </div>
    </DashboardLayout>
  );
}

client/src/pages/SettingsPage.tsx.content:
import { useState, useEffect } from "react";
import { Card, CardContent, CardDescription, CardHeader, CardTitle } from "@/components/ui/card";
import { Button } from "@/components/ui/button";
import { Input } from "@/components/ui/input";
import { Label } from "@/components/ui/label";
import { useToast } from "@/hooks/use-toast";
import { Loader2, Save } from "lucide-react";
import DashboardLayout from "@/components/DashboardLayout";

export default function SettingsPage() {
  const { toast } = useToast();
  const [isLoading, setIsLoading] = useState(false);
  const [isFetching, setIsFetching] = useState(true);
  const [formData, setFormData] = useState({
    fullName: "",
    email: "",
    username: "",
  });

  useEffect(() => {
    const fetchUserData = async () => {
      try {
        const userId = localStorage.getItem("userId");
        if (!userId) return;

        const response = await fetch(`/api/user/${userId}`);
        if (!response.ok) throw new Error("Failed to fetch user data");

        const data = await response.json();
        setFormData({
          fullName: data.fullName || "",
          email: data.email || "",
          username: data.username || "",
        });
      } catch (error) {
        console.error("Error fetching user data:", error);
        toast({
          title: "Error",
          description: "Failed to load user data",
          variant: "destructive",
        });
      } finally {
        setIsFetching(false);
      }
    };

    fetchUserData();
  }, [toast]);

  const handleSubmit = async (e: React.FormEvent) => {
    e.preventDefault();
    setIsLoading(true);

    try {
      const userId = localStorage.getItem("userId");
      if (!userId) throw new Error("Not authenticated");

      const response = await fetch(`/api/user/${userId}`, {
        method: "PATCH",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify({
          fullName: formData.fullName,
        }),
      });

      if (!response.ok) throw new Error("Failed to update profile");

      localStorage.setItem("userName", formData.fullName);

      toast({
        title: "Settings updated",
        description: "Your profile has been updated successfully",
      });
    } catch (error) {
      toast({
        title: "Update failed",
        description: error instanceof Error ? error.message : "Could not update settings",
        variant: "destructive",
      });
    } finally {
      setIsLoading(false);
    }
  };

  if (isFetching) {
    return (
      <DashboardLayout>
        <div className="flex items-center justify-center py-12">
          <Loader2 className="h-8 w-8 animate-spin text-primary" />
        </div>
      </DashboardLayout>
    );
  }

  return (
    <DashboardLayout>
      <div className="max-w-2xl mx-auto p-6">
        <div className="mb-8">
          <h1 className="font-display font-bold text-3xl mb-2">Settings</h1>
          <p className="text-muted-foreground">Manage your account preferences and profile</p>
        </div>

        <Card>
          <CardHeader>
            <CardTitle>Profile Information</CardTitle>
            <CardDescription>Update your personal details</CardDescription>
          </CardHeader>
          <CardContent>
            <form onSubmit={handleSubmit} className="space-y-4">
              <div className="space-y-2">
                <Label htmlFor="fullName">Full Name</Label>
                <Input
                  id="fullName"
                  data-testid="input-fullname"
                  type="text"
                  placeholder="John Doe"
                  value={formData.fullName}
                  onChange={(e) => setFormData({ ...formData, fullName: e.target.value })}
                />
              </div>

              <div className="space-y-2">
                <Label htmlFor="username">Username</Label>
                <Input
                  id="username"
                  data-testid="input-username"
                  type="text"
                  placeholder="johndoe"
                  value={formData.username}
                  onChange={(e) => setFormData({ ...formData, username: e.target.value })}
                  disabled
                />
                <p className="text-sm text-muted-foreground">Username cannot be changed</p>
              </div>

              <div className="space-y-2">
                <Label htmlFor="email">Email</Label>
                <Input
                  id="email"
                  data-testid="input-email"
                  type="email"
                  placeholder="john@example.com"
                  value={formData.email}
                  onChange={(e) => setFormData({ ...formData, email: e.target.value })}
                  disabled
                />
                <p className="text-sm text-muted-foreground">Email cannot be changed</p>
              </div>

              <Button
                type="submit"
                disabled={isLoading}
                data-testid="button-save-settings"
              >
                {isLoading ? (
                  <>
                    <Loader2 className="h-4 w-4 mr-2 animate-spin" />
                    Saving...
                  </>
                ) : (
                  <>
                    <Save className="h-4 w-4 mr-2" />
                    Save Changes
                  </>
                )}
